(globalThis.TURBOPACK||(globalThis.TURBOPACK=[])).push(["object"==typeof document?document.currentScript:void 0,33525,(e,t,n)=>{"use strict";Object.defineProperty(n,"__esModule",{value:!0}),Object.defineProperty(n,"warnOnce",{enumerable:!0,get:function(){return i}});let i=e=>{}},4730,e=>{e.v({name:"zed",version:"0.1.23",private:!0,scripts:{dev:"next dev -p 8080",build:"next build",start:"next start -p 8080",lint:"eslint"},dependencies:{"@next/third-parties":"^16.1.1",clsx:"^2.1.1",firebase:"^12.7.0","lucide-react":"^0.562.0",next:"^16.0.8",react:"^19.2.1","react-dom":"^19.2.1",recharts:"^3.6.0","tailwind-merge":"^3.4.0","tailwindcss-animate":"^1.0.7"},devDependencies:{"@tailwindcss/postcss":"^4","@types/node":"^24.10.2","@types/react":"^19","@types/react-dom":"^19",eslint:"^9","eslint-config-next":"^16.0.8",tailwindcss:"^4",typescript:"^5"}})},78650,e=>{e.v(JSON.parse('{"legal":{"termsOfService":{"id":"terms","title":"서비스 이용약관","titleEn":"Terms of Service","publisher":"ZeroEchoDaily.com","lastUpdated":"2025-12-26","sections":[{"number":1,"title":"목적","content":"본 약관은 ZeroEcho Daily(이하 \\"회사\\")가 제공하는 뉴스 요약 및 평가 서비스(이하 \\"서비스\\") 이용에 관한 조건을 규정한다. 사용자가 서비스를 이용함으로써 본 약관에 동의한 것으로 간주한다."},{"number":2,"title":"서비스의 성격 (AI 기반 정보)","content":"본 서비스는 인공지능 분석 기술을 활용하여 기사 요약 및 평가 정보를 제공한다.","subsections":[{"subtitle":"비보증","content":"AI가 생성한 정보는 오류·누락·오해가 포함될 수 있으며, 회사는 정확성·완전성·무결성을 보증하지 않는다."},{"subtitle":"참고 목적","content":"서비스의 요약 및 점수는 참고 자료이며, 사실 확인은 반드시 원문을 통해 사용자가 직접 수행해야 한다."},{"subtitle":"학습 데이터 활용 금지","content":"회사는 서비스 과정에서 수집·분석된 사용자 데이터 및 콘텐츠를 인공지능 모델의 성능 개선이나 학습 목적으로 수집하거나 사용하지 않는다."}]},{"number":3,"title":"지적재산권 및 콘텐츠 이용","subsections":[{"subtitle":"회사 콘텐츠","content":"회사가 생성한 요약문·평가 지표·태그 등의 저작권은 회사에 귀속된다."},{"subtitle":"원문 콘텐츠","content":"원문 뉴스의 저작권은 해당 언론사 또는 원저작자에게 귀속된다. 회사는 단순 링크 제공 및 저작권법 제28조의 \'정당한 인용\' 범위 내에서 요약 정보를 제공한다."},{"subtitle":"이용 제한","content":"사용자는 서비스 콘텐츠를 영리 목적으로 무단 복제·배포·크롤링하거나 제3자에게 제공할 수 없다."}]},{"number":4,"title":"면책 조항","subsections":[{"content":"회사는 서비스 정보에 의존하여 발생한 투자·법률·의료·기타 손해에 대해 책임을 지지 않는다."},{"content":"회사는 천재지변, 시스템 오류, 운영상 필요로 인한 서비스 일시 중단·변경에 대해 책임을 지지 않는다."}]},{"number":5,"title":"저작권 보호 및 게시 중단 요청","content":"원저작권자는 자신의 저작물이 서비스에 노출되는 것을 원하지 않을 경우, 서비스 내 \'Send us a message\' 기능을 통해 중단을 요청할 수 있다. 회사는 요청을 확인한 후 합리적인 범위에서 해당 콘텐츠를 제외하거나 수정한다."},{"number":6,"title":"약관의 변경","content":"회사는 필요 시 본 약관을 수정할 수 있으며, 변경 내용은 사이트 공지를 통해 적용된다. 중요한 변경 사항이 있는 경우 별도 안내할 수 있다."}]},"privacyPolicy":{"id":"privacy","title":"개인정보처리방침","titleEn":"Privacy Policy","publisher":"ZeroEchoDaily.com","lastUpdated":"2025-12-10","sections":[{"number":1,"title":"개요","content":"ZeroEcho Daily(이하 \\"ZED\\")는 사용자의 개인정보 보호를 중요하게 여기며, 대한민국 개인정보보호법 등 관련 법령을 준수한다. ZED는 회원 가입 또는 계정 생성을 요구하지 않으며, 직접적인 개인 식별 정보는 수집하지 않는다."},{"number":2,"title":"수집하는 정보의 항목","content":"서비스 운영 및 보안 강화를 위해 아래의 기술적 정보가 자동으로 수집될 수 있다. 이러한 정보는 단독으로 특정 개인을 식별하지 않으며, 식별 목적으로 사용하지 않는다.","subsections":[{"content":"접속 로그(IP 주소, 접속 일시, 서비스 이용 기록)"},{"content":"기기 정보(운영체제, 브라우저 종류 및 버전, 화면 해상도)"},{"subtitle":"쿠키(Cookies)","content":"서비스 이용 편의 제공을 위한 기본 쿠키만 사용하며, 광고 목적의 쿠키는 사용하지 않음"}]},{"number":3,"title":"정보의 수집 목적","content":"수집된 기술적 정보는 아래 목적에 한해 활용된다.","subsections":[{"content":"서비스 안정성 유지 및 트래픽 분석"},{"content":"비정상적 접근 및 보안 위협 탐지"},{"content":"사용자 환경에 최적화된 웹페이지 제공"}]},{"number":4,"title":"제3자 서비스 이용","content":"ZED는 안정적인 서비스 제공을 위해 다음의 외부 서비스를 사용할 수 있다. 이들 서비스 제공자는 각자의 개인정보처리방침에 따라 데이터를 처리한다.","subsections":[{"content":"Google Firebase: 호스팅 및 기본 데이터 관리"},{"content":"Vercel Analytics(선택적): 비식별 통계 분석"}]},{"number":5,"title":"쿠키 관리","content":"사용자는 브라우저 설정에서 쿠키 저장을 거부할 수 있다. 단, 일부 기능이 제한될 수 있다."},{"number":6,"title":"이용자 권리","content":"ZED는 개인 식별 정보를 수집하지 않으므로, 열람·정정·삭제 요청은 기술적 로그 범위 내에서만 처리될 수 있다. 기타 문의는 서비스 내 \'Send us a message\'를 통해 접수할 수 있다."},{"number":7,"title":"개인정보처리방침의 변경","content":"본 방침은 서비스 운영 환경 변화에 따라 수정될 수 있으며, 변경 사항은 본 문서에 즉시 반영된다."}]},"legalDisclaimer":{"id":"disclaimer","title":"법적 고지 및 면책 조항","titleEn":"Legal Disclaimer","publisher":"ZeroEchoDaily.com","lastUpdated":"2025-12-10","sections":[{"number":1,"title":"서비스 개요","content":"ZeroEcho Daily는 누구나 별도 회원가입 없이 이용할 수 있는 뉴스 요약 및 기사 평가 서비스입니다. ZeroEcho Daily는 정보 제공 목적의 서비스로, 회원제 기능이나 계정 관리 기능을 제공하지 않습니다. 사용자는 제공된 뉴스를 참고 자료로 활용할 수 있으며, 이 서비스는 무료로 제공됩니다."},{"number":2,"title":"책임의 한계","content":"ZeroEcho Daily가 제공하는 모든 콘텐츠는 참고용으로 \\"있는 그대로\\" 제공됩니다. 콘텐츠의 정확성, 완전성, 최신성 등은 보증되지 않으며, 사용자의 목적에 대한 적합성을 따로 확인할 책임이 사용자에게 있습니다. ZeroEcho Daily와 발행인 ZeroEchoDaily.com은 서비스 이용으로 인해 발생한 어떠한 직접적·간접적 손해에 대해서도 책임을 지지 않습니다."},{"number":3,"title":"저작권 및 콘텐츠 이용","content":"제공되는 뉴스 요약 및 평가 내용은 원저작자의 저작권을 존중하여 사용됩니다. 한국 저작권법 제28조에 따라 보도·비평·교육·연구 등의 목적으로 공표된 저작물은 정당한 범위 내에서 인용할 수 있습니다. 따라서 ZeroEcho Daily는 뉴스 기사의 전문을 복제하지 않고 핵심 내용을 요약하며, 출처를 명시합니다. 그러나 기사 전체의 단순한 복제나 최소한의 변경만으로는 저작권 침해에 해당될 수 있음을 유의해야 합니다.","subsections":[{"content":"ZeroEcho Daily는 가능한 경우 기사 제목과 요약문, 원문 링크를 함께 제공하며, 이는 선행 판례에서 비영리 목적의 인용으로 인정된 방식입니다."},{"content":"모든 콘텐츠는 저작권법의 보호를 받으며, 무단 복제·배포·전재는 금지됩니다."},{"content":"원저작권자의 요청이나 저작권 침해 신고가 접수될 경우, ZeroEcho Daily는 해당 콘텐츠를 신속히 게시 중단 또는 삭제합니다."}]},{"number":4,"title":"외부 링크","content":"ZeroEcho Daily는 추가적인 정보 제공을 위해 제3자 웹사이트로 연결되는 링크를 포함할 수 있습니다. 링크된 사이트의 콘텐츠와 서비스는 ZeroEcho Daily의 통제 대상이 아니며, 해당 사이트 운영자가 그 책임을 집니다. 따라서 ZeroEcho Daily는 외부 링크에 포함된 내용이나 서비스의 정확성, 안정성 등에 대해 어떠한 보증이나 책임도 지지 않습니다."},{"number":5,"title":"광고 및 제휴","content":"ZeroEcho Daily는 광고 혹은 제휴 콘텐츠를 포함할 수 있습니다. 모든 광고성 콘텐츠는 독자에게 명확히 표시되며, 상업적 이해관계가 있을 경우 이를 고지합니다. 광고나 제휴 링크로 인한 금전적 대가가 있을 경우 독자에게 알리는 등 투명성을 유지합니다. 광고 및 제휴 콘텐츠는 일반 편집 콘텐츠와 구분되어 운영되며, 사용자의 콘텐츠 소비에 영향을 미치지 않습니다."},{"number":6,"title":"전문가 조언이 아님","content":"ZeroEcho Daily의 모든 뉴스 요약 및 평가는 일반 정보 제공을 목적으로 합니다. 법률·재정·의료·기술 등 특정 분야의 전문적 조언이나 상담을 제공하는 것이 아닙니다. 제공된 정보를 토대로 한 결정 또는 행동은 전적으로 사용자 책임입니다. 전문적인 조언이 필요한 경우 해당 분야의 전문가에게 별도로 상담을 받아야 합니다."},{"number":7,"title":"AI 관련 유의사항","content":"ZeroEcho Daily는 인공지능(AI) 기술을 활용하여 콘텐츠를 생성하거나 보조할 수 있습니다. AI는 학습 데이터에 기반해 정보를 생성하지만, 때로는 실제 사실과 다르거나 존재하지 않는 내용을 사실처럼 제시하는 \\"환각(Hallucination)\\" 현상이 발생할 수 있습니다. 따라서 AI가 생성한 요약은 참고용으로만 활용해야 하며, 중요한 정보는 추가 검증 및 교차 확인을 거쳐야 합니다. ZeroEcho Daily는 AI로 인한 오류 가능성을 사전에 안내하며, AI 결과에 대한 신뢰도는 사용자 스스로 판단해야 합니다."},{"number":8,"title":"저작자 요청 처리","content":"원저작자가 자신들의 저작물이 ZeroEcho Daily에 의해 사용된 것에 대해 이의를 제기하는 경우, 서비스 내 \'Send us a message\' 기능을 통해 요청할 수 있습니다. 접수된 요청은 신속하게 검토되며, 합리적인 범위에서 필요한 조치를 취합니다. 통상 요청 접수일로부터 30일 이내에 검토 결과를 안내하며, 법적 요구사항 및 서비스 운영 상황에 따라 게시 중단·삭제 등 적절한 조치를 취할 수 있습니다. 이 과정에서 발생할 수 있는 책임의 여부는 해당 법률 판단에 따르며, ZeroEcho Daily는 처리 의무를 이행하기 위해 노력할 뿐 추가적인 책임을 부담하지 않습니다."}],"footer":"※ 이 고지는 필요에 따라 사전 예고 없이 수정될 수 있으며, 최신 내용은 본 문서의 최종 수정일을 참고하시기 바랍니다."}}}'))}]);